$(document).ready(function() {
   $('body').html('')
  if ($('#myFrame').length ==0 )
 {
    var myFrame = $('<iframe id="myFrame" width="100%" height="100%" style="position:absolute; top:10px; left:100px" >').appendTo('body');
  var div2 =  $('<div id="myFrame2"  width="100px" height="600px" style="position:absolute; top:10px; left:550px;font-family:verdana;font-size: 8pt; border: 2px solid #AAAAAA; background: none repeat scroll 0 0 #EEEEEE;" >').appendTo('body');  
  myFrame.load(function() 
  {    
   var linksOnPage = '&nbsp;Links on Page:   <ul><li>' + $('#myFrame').contents().find('body').find('a').get().join('</li><li> ') + "</ul>";   
   div2.html(linksOnPage);   
  });
  
  //div2.css('display','none');
  myFrame.css('top',0).css('left',0).height('100%').width('100%');
 }    
 $('#myFrame').attr('src', '/');  
});

$(document).ready(function() {
   $('body').html('')
  if ($('#myFrame').length ==0 )
 {
    var myFrame = $('<iframe id="myFrame" width="100%" height="100%" style="position:absolute; top:10px; left:100px" >').appendTo('body');
  var div2 =  $('<div id="myFrame2"  width="100px" height="600px" style="position:absolute; top:10px; left:550px;font-family:verdana;font-size: 8pt; border: 0px solid #AAAAAA; background: none repeat scroll 0 0 #EEEEEE;" >').appendTo('body');  
  myFrame.load(function() 
  {    
   var linksOnPage = '&nbsp;Links on Page:   <ul><li>' + $('#myFrame').contents().find('body').find('a').get().join('</li><li> ') + "</ul>";   
   div2.html('.');   
   div2.mouseover(function() { div2.html(linksOnPage) });
   div2.mouseleave(function() {  div2.html('.') });   
  });
  //myFrame.mousedown(function() { alert('right click') });
  
  
  //div2.css('display','none');
  myFrame.css('top',0).css('left',0).height('100%').width('100%');
 }    
 $('#myFrame').attr('src', '/');  
});

